import LoanCard from './LoanCard';
import img from './assests/ena';

function LoanGrid() {
  return (
    <section className="loan-grid">
      <h2>وام‌های در جریان</h2>
      <div className="grid">
        {[...Array(6)].map((_, i) => (
          <LoanCard
            key={i}
            img={img}
            title="خرید لوازم التحریر"
            price={8000000}
            isUrgent={i === 2}
          />
        ))}
      </div>
      <button className="show-all-btn">+ دیدن همه</button>
    </section>
  );
}

export default LoanGrid;
