import './LoanCard.css';
import { FaArrowLeft } from 'react-icons/fa';

function LoanCard({ img, title, price, isUrgent }) {
  return (
    <div className="loan-card">
      <img src={img} alt="stationery" />
      <div className="loan-info">
        <h4>{title}</h4>
        <p>خیرین</p>
        {isUrgent && <span className="urgent-tag">فوری</span>}
      </div>
      <div className="loan-footer">
        <button><FaArrowLeft /></button>
        <p>{price.toLocaleString()} تومان</p>
      </div>
      <div className="loan-label">V2</div>
    </div>
  );
}

export default LoanCard;
