import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "./Register.css"
import "./log"
function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleRegister = (e) => {
    e.preventDefault();
    const users = JSON.parse(localStorage.getItem('users')) || [];
    if (users.find(u => u.username === username)) {
      alert('این نام کاربری قبلا ثبت شده است');
      return;
    }
    users.push({ username, password });
    localStorage.setItem('users', JSON.stringify(users));
    alert('ثبت نام موفقیت آمیز بود');
    navigate('/login');
  };

  return (
    <div className="login-container">
    <img style={{width: '100%'}} src="/assests/logo.png" alt="لوگو" className="logo" />

      <div className="tabs">
        <button className="tab active">وام‌دهنده</button>
        <button className="tab">گروه جهادی</button>
      </div>

      <form className="login-form" onSubmit={handleLogin}>
        <h2>ورود</h2>


        <button type="submit" className="login-button">
          <span>ورود</span>
          <span className="arrow">←</span>
        </button>
      </form>

      <div className="register-link">
        حساب کاربری ندارید؟ از طریق این لینک <a href="/signup">ثبت‌نام</a> کنید.
      </div>
    </div>

  );
}

export default Register;
